import React from 'react';
import WomanImg from '../img/about/woman.png';
import { Link } from 'react-router-dom';

const About = () => {
  return (
    <section className='section'>
      <div className='container mx-auto h-full relative'>
        {/* text image wrapper */}
        <div className='flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16'>
          {/* text */}
          <div className='flex-1 pt-'>
            <h1 className='h1'>About Me</h1>
            <p>
              In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum.
            </p>
            <br />
            <br />
            <p>
              My name is John Doe, and I am a freelance photographer and film maker. I have been traveling the world capturing the beauty of nature and sharing it with others. I believe that every moment, every shot, is a chance to inspire and provoke thought.
            </p>
          </div>
          {/* image */}
          <div className='flex-1 max-h-96 lg:max-h-max order-none overflow-hidden'>
            <img className='w-full h-auto' src={WomanImg} alt="About Woman" />
          </div>
          <Link to = {'/Portfolio'}> View My Work </Link>
        </div>
      </div>
    </section>
  );
};

export default About;
