import React from 'react';
import viberr from '/home/skanda/Desktop/coding/Freelance/Anika Saxena Portfolio/src/img/portfolio/1.png';
import newProjectImage from '/home/skanda/Desktop/coding/Freelance/Anika Saxena Portfolio/src/img/portfolio/2.png'; // Import the new image
import { motion } from 'framer-motion';

// ProjectCard Component
const ProjectCard = ({ src, link, h3, p }) => (
  <motion.a 
    href={link} 
    target="_blank" 
    rel="noopener noreferrer" 
    className='flex flex-col items-center text-center transition-transform duration-200 ease-in-out transform hover:scale-105'
  >
    <motion.img 
      src={src} 
      alt={h3} 
      className='max-w-[250px] bg-project-card-bg rounded-2xl'
    />
    <h3 className='mt-4 text-xl font-semibold'>{h3}</h3>
    <p className='mt-2 text-base'>{p}</p>
  </motion.a>
);

const projects = [
  {
    src: viberr,
    link: "https://github.com/Ade-mir/company-landing-page-2",
    h3: "Viberr",
    p: "Streaming App",
  },
  {
    src: newProjectImage,
    link: "https://github.com/username/new-project", // Replace with the actual link
    h3: "New Project",
    p: "Description of the new project.",
  },
  // You can add more projects here
];

function Projects() {
  return (
    <section className='section bg-gray-100 flex items-center justify-center min-h-screen'>
      <div className='container mx-auto px-4 py-16'>
        <div className='flex flex-col items-center gap-y-12 text-center'>
          <h1 className='h1'>Projects</h1>
          <p className='text-lg'>
            Here are some of my recent projects. Click on the images to learn more!
          </p>
          <div className='flex flex-col items-center gap-8 md:flex-row md:flex-wrap md:justify-evenly p-0 md:p-8'>
            {projects.map((project, index) => (
              <ProjectCard
                key={index}
                src={project.src}
                link={project.link}
                h3={project.h3}
                p={project.p}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default Projects;
