import React from 'react';
import WomanImg from '../img/home/woman.png'; // Adjust this path if needed
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import {transition1} from '../transitions'

const Home = () => {
  return (
    <motion.section 
    initial={{opacity: 0}}
    animate={{opacity: 1}}
    exit = {{opacity: 0}}
    transition={transition1}
    className='section'>
      <div className='container mx-auto h-full relative flex flex-col lg:flex-row items-center'>
        {/* Text and image wrapper */}
        <div className='flex flex-col justify-center w-full lg:w-1/2 lg:pr-10'>
          {/* Text */}
          <motion.div
          initial={{opacity: 0, y: '-50'}}
          animate={{opacity: 1}}
          exit = {{opacity: 0}}
          transition={transition1}
          className='pt-36 pb-14 lg:pt-0 lg:pb-0 z-10 flex flex-col justify-center items-center lg:items-start'>
            <h1 className='h1'>Photographer <br /> & Film Maker</h1>
            <p className='text-[26px] lg:text-[36px] font-primary mb-4 lg:mb-12'>Los Angeles</p>
            <Link to='/contact' className='btn mb-[30px]'>Hire me</Link>
          </motion.div>
        </div>        {/* Image */}
        <div className='flex justify-center lg:justify-end w-full lg:w-1/2'>
          <img src={WomanImg} alt="Photographer" className='max-w-full h-auto' />
        </div>
      </div>
    </motion.section>
  );
};

export default Home;
