import React from 'react';
import viberr from '/home/skanda/Desktop/coding/Freelance/Anika Saxena Portfolio/src/img/portfolio/1.png';
import { motion } from 'framer-motion';
import { transition1 } from '../transitions';

// ProjectCard Component
const ProjectCard = ({ src, link, h3, p }) => (
  <motion.a 
    href={link} 
    className='flex flex-col items-center text-center transition-transform duration-200 ease-in-out transform hover:scale-105'
  >
    <motion.img 
      src={src} 
      alt={h3} 
      className='max-w-[250px] bg-project-card-bg rounded-2xl'
    />
    <h3 className='mt-4 text-xl font-semibold'>{h3}</h3>
    <p className='mt-2 text-base'>{p}</p>
  </motion.a>
);

function Projects() {
  return (
    <section id="projects" className='flex flex-col items-center'>
      <h1 className='sectionTitle text-3xl font-bold mb-8'>Projects</h1>
      <div className='flex flex-col items-center gap-8 md:flex-row md:flex-wrap md:justify-evenly p-0 md:p-8'>
        <ProjectCard
          src={viberr}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="Viberr"
          p="Streaming App"
        />
      </div>
    </section>
  );
}

export default Projects;
