import React from 'react';

// Import images
import Image1 from '../img/portfolio/1.png';
import Image2 from '../img/portfolio/2.png';
import Image3 from '../img/portfolio/3.png';
import Image4 from '../img/portfolio/4.png';

// Import Link
import { Link } from 'react-router-dom';

// Array of images for easier mapping
const images = [Image1, Image2, Image3, Image4];

const Portfolio = () => {
  return (
    <section className='section'>
      <div className='container mx-auto h-full relative'>
        <div className='flex flex-col lg:flex-row h-full items-center justify-start gap-x-24 text-center lg:text-left pt-24 lg:pt-36 pb-8'>
          {/* Text */}
          <div>
            <h1 className='h1'>Portfolio</h1>
            <p className='mb-12 max-w-sm'>
              In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum.
              <br />
              <br />
              My name is John Doe, and I am a freelance photographer and filmmaker. I have been traveling the world capturing the beauty of nature and sharing it with others. I believe that every moment, every shot, is a chance to inspire and provoke thought.
            </p>
            <div className='mt-6'>
              <Link to='/Portfolio' className='btn'>Hire Me</Link>
            </div>
            <br />
          </div>
          {/* Image grid */}
          <div className='grid grid-cols-2 gap-0 lg:gap-4'>
            {images.map((image, index) => (
              <div key={index} className='max-w-[250px] lg:max-w-[320px] h-[187px] lg:h-[220px] bg-accent overflow-hidden'>
                <img 
                  className='object-cover h-full lg:h-[220px] hover:scale-110 transition-all duration-500'
                  src={image}
                  alt={`Portfolio ${index + 1}`}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
