import React from 'react';
import WomanImg from '/home/skanda/Desktop/coding/Freelance/Anika Saxena Portfolio/src/img/about/Untitled.jpeg';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { transition1 } from '../transitions'; // Make sure this import is correct

const About = () => {
  return (
    <motion.section
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      transition={transition1}
      className='section'
    >
      <div className='container mx-auto h-full relative'>
        {/* text image wrapper */}
        <div className='flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16'>
          {/* text on mobile first */}
          <motion.div
            initial={{ opacity: 0, y: '-50%' }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: '-50%' }}
            transition={transition1}
            className='flex-1 pt-36 pb-14 lg:pt-0 lg:w-auto z-10 flex flex-col justify-center items-center lg:items-start order-1 lg:order-2'
          >
            <header>
              <h1 className='h1'>About Me</h1>
            </header>
            <p className='mb-12 max-w-sm'>
              In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum.
            </p>
            <p>
              My name is John Doe, and I am a freelance photographer and filmmaker. I have been traveling the world capturing the beauty of nature and sharing it with others. I believe that every moment, every shot, is a chance to inspire and provoke thought.
            </p>
            <div className='mt-6'>
              <Link to={'/Portfolio'} className='btn'>View My Work</Link>
            </div>
          </motion.div>

          {/* image on mobile last */}
          <motion.div
            className='flex-1 max-h-96 lg:max-h-max order-2 lg:order-1 overflow-hidden gap-2'
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0 }}
            whileHover={{ scale: 1.1 }}
            transition={transition1}
          >
            <img className='w-full h-auto' src={WomanImg} alt="A woman representing creativity and inspiration" />
          </motion.div>
        </div>
      </div>
    </motion.section>
  );
};

export default About;
