// CSS styles as a string
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'center',
  },
  img: {
    maxWidth: '250px',
    alignSelf: 'center',
    backgroundColor: 'var(--project-card-bg)',
    borderRadius: '20px',
  },
  a: {
    width: 'fit-content',
    textDecoration: 'none',
    color: '#222',
    transition: 'transform 200ms ease-in-out',
  },
  aHover: {
    transform: 'scale(1.05)',
  },
  projectsContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '30px',
  },
};

// Media query handling (basic implementation)
const mediaQueryStyles = {
  '@media (min-width: 800px)': {
    projectsContainer: {
      flexDirection: 'row',
      flexWrap: 'wrap',
      justifyContent: 'space-evenly',
      padding: '0 30px',
    },
  },
};

function Projects() {
  return (
    <section id="projects" style={styles.container}>
      <h1 className="sectionTitle">Projects</h1>
      <div style={styles.projectsContainer}>
        <ProjectCard
          src={viberr}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="Viberr"
          p="Streaming App"
        />
        <ProjectCard
          src={freshBurger}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="Fresh Burger"
          p="Hamburger Restaurant"
        />
        <ProjectCard
          src={hipsster}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="Hipsster"
          p="Glasses Shop"
        />
        <ProjectCard
          src={fitLift}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="FitLift"
          p="Fitness App"
        />
      </div>
      <style>{`
        img {
          max-width: 250px;
          align-self: center;
          background-color: var(--project-card-bg);
          border-radius: 20px;
        }

        a {
          width: fit-content;
          text-decoration: none;
          color: #222;
          transition: transform 200ms ease-in-out;
        }

        a:hover {
          transform: scale(1.05);
        }

        @media (min-width: 800px) {
          .projectsContainer {
            flex-direction: row;
            flex-wrap: wrap;
            justify-content: space-evenly;
            padding: 0 30px;
          }
        }
      `}</style>
    </section>
  );
}

export default Projects;
