import React from 'react';
import WomanImg from '../img/about/woman.png';
import { Link } from 'react-router-dom';

const About = () => {
  return (
    <section className='section'>
      <div className='container mx-auto h-full relative'>
        {/* text image wrapper */}
        <div className='flex flex-col lg:flex-row h-full items-center justify-center gap-x-24 text-center lg:text-left lg:pt-16'>
          {/* image on left */}
          <div className='flex-1 max-h-96 lg:max-h-max order-1 overflow-hidden gap-2'>
            <img className='w-full h-auto' src={WomanImg} alt="A woman representing creativity and inspiration" />
          </div>
          {/* text 
          To change the 
          */}
          <div className='flex-1 pt-36 pb-14 lg:pt-0 lg:w-auto z-10 flex flex-col justify-center items-center lg:items-start order-2'>
            <header>
              <h1 className='h1'>About Me</h1>
            </header>
            <p className='mb-12 max-w-sm'>
              In publishing and graphic design, Lorem ipsum is a placeholder text commonly used to demonstrate the visual form of a document or a typeface without relying on meaningful content. Lorem ipsum.
            </p>
            <p>
              My name is John Doe, and I am a freelance photographer and filmmaker. I have been traveling the world capturing the beauty of nature and sharing it with others. I believe that every moment, every shot, is a chance to inspire and provoke thought.
            </p>
            <div className='mt-6'> {/* Added margin-top for spacing */}
              <Link to={'/Portfolio'} className='btn'>View My Work</Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
