import React from 'react';
import viberr from '/home/skanda/Desktop/coding/Freelance/Anika Saxena Portfolio/src/img/portfolio/1.png';

// CSS styles as a string
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'center',
  },
  img: {
    maxWidth: '250px',
    alignSelf: 'center',
    backgroundColor: 'var(--project-card-bg)',
    borderRadius: '20px',
  },
  a: {
    width: 'fit-content',
    textDecoration: 'none',
    color: '#222',
    transition: 'transform 200ms ease-in-out',
  },
  aHover: {
    transform: 'scale(1.05)',
  },
  projectsContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '30px',
  },
};

// ProjectCard Component
const ProjectCard = ({ src, link, h3, p }) => (
  <a href={link} style={styles.a}>
    <img src={src} alt={h3} style={styles.img} />
    <h3>{h3}</h3>
    <p>{p}</p>
  </a>
);

function Projects() {
  return (
    <section id="projects" style={styles.container}>
      <h1 className="sectionTitle">Projects</h1>
      <div style={styles.projectsContainer}>
        <ProjectCard
          src={viberr}
          link="https://github.com/Ade-mir/company-landing-page-2"
          h3="Viberr"
          p="Streaming App"
        />
      </div>
      <style>{`
        @media (min-width: 800px) {
          #projects div {
            flex-direction: row;
            flex-wrap: wrap;
            justify-content: space-evenly;
            padding: 0 30px;
          }
        }
      `}</style>
    </section>
  );
}

export default Projects;
